const qpdf = require('../dist/index');
const fs = require("fs");

const sample = "./test/sample.pdf";
const encryptedFile = "./test/encrypted.pdf";
const decryptedFile = "./test/decrypted.pdf";
const password = "1234";
const options = {
  password,
}

// Test Encrypt from a file to a File
qpdf.encrypt(sample, options, "./test/file-to-file.pdf");

// Test file to buffer
qpdf.encrypt(sample, options).then((data) => {
  fs.writeFile("./test/file-to-buffer.pdf",Buffer.from(data),{encoding: 'binary'},(data) => {
    console.log("Done",data);
  });
});

